import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getAllUsers } from '../features/auth/authSlice'

const AllUsers = () => {
    const dispatch = useDispatch()
const {users} = useSelector(state => state.user)
    
    useEffect(() => {
    dispatch(getAllUsers())
},[])
  return (
    <div className='p-9 '>
    <table className="mx-auto table-auto w-9/12 bg-purple-700 text-white">
    <thead className='bg-purple-900 h-[3rem]'>
      <tr>
        <th className='text-2xl'>Task</th>
        <th className='text-2xl'>Deadline</th>
        <th className='text-2xl'>Status</th>
      </tr>
    </thead>
    <tbody className='space-y-4 !px-10'>
{users?.map((tas) => {
  return (
      <tr className=' border !h-[3rem]' key={tas?._id}>
        <td> {tas?.task}</td>
        <td> { tas?.deadline}</td>
        <td className='rounded py-1 bg-pink-400'> {tas?.status  } </td>
      </tr>

)      })}      
</tbody>
</table>
</div>


  )
}

export default AllUsers
