import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { getTasks } from '../features/task/taskSlice'

const UserDashboard = () => {
const dispatch = useDispatch()

  const { task } = useSelector(state => state.task)
  useEffect(() => {
      dispatch(getTasks())
  }, [])
  return (
      <div>
          <table className="table-auto w-screen bg-purple-700">
          <thead>
            <tr>
              <th className='text-2xl'>Task</th>
              <th className='text-2xl'>Deadline</th>
              <th className='text-2xl'>Status</th>
            </tr>
          </thead>
          <tbody className='space-y-4 !px-10'>
      {task?.map((tas) => {
        return (
            <tr className=' border !h-[3rem]' key={tas?._id}>
              <td> {tas?.task}</td>
              <td> { tas?.deadline}</td>
              <td className='rounded py-1 bg-orange-300'> {tas?.status  } </td>
            </tr>
 
 )      })}      
 </tbody>
</table>
    </div>
  )
}

export default UserDashboard
