import axios from "axios";

const API_URL = "api/tasks/";
const token = localStorage.getItem("user");
// Create new GOAL

const createTask = async (goalData) => {
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };
  console.log(goalData, token)
  const response = await axios.post(API_URL, goalData, config);

  return response.data;
};

// get all the goals

const getTasks = async (token) => {
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };
  const response = await axios.get(API_URL, config);
  return response.data;
};

 
const getAllTasks = async () => { 
    const config = {
        headers: {
            Authorization: `Bearer ${"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzZjRkYzZiOWVmZDE3MGFjYTliN2IxOCIsImlhdCI6MTY3NzA2MDQyOSwiZXhwIjoxNjc5NjUyNDI5fQ.x9ds0Rlp9Y_OWWCqowNW_2ebJTy6MGF6WcL9LgnsOJ8"}`,
        }
    }
    const res = await axios.get("http://localhost:8000/api/tasks/all", config);
    return res.data;
}

const updateTaskStatus = async () =>{}
const goalServices = {
  createTask,
    getTasks,
    getAllTasks,
  updateTaskStatus,
};

export default goalServices;
